import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import LoadingSpinner from './LoadingSpinner';
import FileInfo from './FileInfo';
import uploadIcon from '../uploadIcon.svg';

const DragAndDropUpload = () => {
  const [tags, setTags] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [fileInfo, setFileInfo] = useState(null);
  const [ws, setWs] = useState(null);

  const onDrop = useCallback((acceptedFiles) => {
    const file = acceptedFiles[0];
    setFileInfo({
      name: file.name,
      size: (file.size / 1024 / 1024).toFixed(2),
    })

    const formData = new FormData();
    formData.append('video', file);

    setIsUploading(true);

    axios.post('http://localhost:5001/api/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
    .then(response => {
      const ws = new WebSocket('ws://localhost:5001');
      setWs(ws);

      ws.onopen = () => {
        ws.send(JSON.stringify({ action: 'process', filePath: response.data.filePath }));
      }

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.progress) {
          setUploadProgress(data.progress);
        }
        if (data.tags) {
          setTags(data.tags);
          setIsUploading(false);
        }
        if (data.error) {
          console.error(data.error);
          setIsUploading(false);
        }
      }

      ws.onclose = () => {
        setWs(null);
      }
    })
    .catch(error => {
      console.error('Error uploading the file:', error);
      setIsUploading(false);
    })
  }, []);

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({ 
    onDrop,
    noClick: true, 
    noKeyboard: true,
    disabled: isUploading,
  });

  return (
    <div className='flex flex-col items-center px-10 py-6 md:border-gray-200 md:border md:border-solid md:rounded-md max-w-screen-md mx-auto'>
      <h2 className='font-bold mb-1 text-xl self-start'>Upload your Video</h2>
      <p className='font-semibold text-sm mb-6 self-start text-gray-600'>Upload a video to generate metadata keywords based on its transcript.</p>
      <div 
        className={`flex flex-col items-center justify-center bg-gray-50 border-gray-400 border-2 rounded-md p-10 mb-6 w-full min-h-60 transition-colors duration-300 ${isDragActive ? 'bg-indigo-100/50 border-indigo-400 border-solid' : 'border-gray-400 border-dashed'} ${isUploading ? 'opacity-50 cursor-not-allowed' : ''}`} 
        {...getRootProps()}
      >
        <input {...getInputProps()} disabled={isUploading} />
        {
          isUploading ? (
            <LoadingSpinner/>
          ) : (
            <div className='flex flex-col items-center'>
              <div className='bg-indigo-500 p-2 rounded-md w-12 mb-4'>
                <img src={uploadIcon} alt="A white upload icon - a white arrow pointing up" />
              </div>
              <p className={`font-bold text-lg text-center transition-opacity ${isDragActive ? 'opacity-100=0' : 'opacity-0'}`}>
                {`${isDragActive ? 'Drop to upload your file' : 'Drag a video file here to upload it'}`}
              </p>
              <button type='button'className={`font-semibold text-indigo-600 hover:cursor-pointer transition-transform transform ${isDragActive ? 'scale-0' : 'scale-100'}`} onClick={open}>Or choose your file</button>
            </div>
          )
        }
      </div>
      {fileInfo && <FileInfo fileInfo={fileInfo} uploadProgress={uploadProgress} />}
      <div className='flex flex-col w-full'>
          <h3 className='uppercase text-sm mb-1'>Keywords:</h3>
          <ul className='flex flex-wrap'>
            {tags.map((tag, index) => (
              <div key={index} className='font-semibold bg-indigo-50 rounded-md mr-2 mt-2 px-3 py-2'>
                <li className='inline-block transition-opacity opacity-100'>{tag}</li>
              </div>
            ))}
          </ul>
        </div>
    </div>
  );
};

export default DragAndDropUpload;
