import React, { useState } from 'react';
import axios from 'axios';

const UploadVideo = () => {
  const [file, setFile] = useState(null);
  const [tags, setTags] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('video', file);

    try {
      const response = await axios.post('http://localhost:5001/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setTags(response.data.tags);
    } catch (error) {
      console.error('Error uploading the file:', error);
    }
  };

  return (
    <div className='flex flex-col items-center p-10'>
      <h2 className='font-bold'>Upload your Video</h2>
      <div className='bg-gray-100 border-gray-400 border-dashed border-2 rounded-sm'>
        <p>Drag and drop your video file here or <label className="font-semibold underline hover:cursor-pointer" for="fileChooser">Choose a File</label> to upload</p>
        <input type="file" id="fileChooser" className= "hidden" onChange={handleFileChange} />
        <button onClick={handleUpload}>Upload</button>
      </div>
      <div>
        <h3>Generated Tags:</h3>
        <ul>
          {tags.map((tag, index) => (
            <li key={index}>{tag}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default UploadVideo;
