import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import LoadingSpinner from './LoadingSpinner';
import uploadIcon from '../uploadIcon.svg';

const DragAndDropUpload = () => {
  const [tags, setTags] = useState(["example-tag-1", "example-tag-2"]);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [fileInfo, setFileInfo] = useState(null);
  const [ws, setWs] = useState(null);

  const onDrop = useCallback((acceptedFiles) => {
    const file = acceptedFiles[0];
    setFileInfo({
      name: file.name,
      size: (file.size / 1024 / 1024).toFixed(2),
    })

    const formData = new FormData();
    formData.append('video', file);

    setIsUploading(true);

    axios.post('http://localhost:5001/api/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
    .then(response => {
      const ws = new WebSocket('ws://localhost:5001');
      setWs(ws);

      ws.onopen = () => {
        ws.send(JSON.stringify({ action: 'process', filePath: response.data.filePath }));
      }

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.progress) {
          setUploadProgress(data.progress);
        }
        if (data.tags) {
          setTags(data.tags);
          setIsUploading(false);
        }
        if (data.error) {
          console.error(data.error);
          setIsUploading(false);
        }
      }

      ws.onclose = () => {
        setWs(null);
      }
    })
    .catch(error => {
      console.error('Error uploading the file:', error);
    })
    .finally(() => {
      setIsUploading(false);
    });
  }, []);

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({ 
    onDrop,
    noClick: true, 
    noKeyboard: true
  });

  return (
    <div className='flex flex-col items-center px-10 py-6 md:border-gray-200 md:border md:border-solid md:rounded-md max-w-screen-md mx-auto'>
      <h2 className='font-bold mb-1 text-xl self-start'>Upload your Video</h2>
      <p className='font-semibold text-sm mb-6 self-start text-gray-600'>Upload a video to generate metadata tags based on its transcript.</p>
      <div className={`flex flex-col items-center justify-center bg-gray-50 border-gray-400 border-2 rounded-md p-10 mb-6 w-full min-h-60 transition-colors duration-300 ${isDragActive ? 'bg-indigo-100/50 border-indigo-400 border-solid' : 'border-gray-400 border-dashed'}`} {...getRootProps()}>
        <input {...getInputProps()} />
        <div className='bg-indigo-500 p-2 rounded-md w-12 mb-4'>
          <img src={uploadIcon} />
        </div>
        <p className='font-bold text-lg text-center'>
          {`${isDragActive ? 'Drop to upload your file' : 'Drag a video file here to upload it for tagging'}`}
        </p>
        <button type='button'className={`font-semibold text-indigo-600 hover:cursor-pointer ${isDragActive && 'hidden'}`} onClick={open}>Or choose your file</button>
        <LoadingSpinner />
        {isUploading && <p>Uploading...</p>}
      </div>
      {fileInfo && (
        <div className='w-full mb-4 p-4 border-gray-200 border border-solid rounded-md'>
          <div className='flex justify-between'>
            <span className='text-sm'>{fileInfo.name}</span>
            <span>{fileInfo.size} MB</span>
          </div>
          <div className='w-full bg-gray-200 h-2 rounded-full mt-2'>
            <div
              className='bg-indigo-500 h-full rounded-full transition-all duration-500 ease-out'
              style={{ width: `${uploadProgress}%` }}
            ></div>
          </div>
          <div className='flex justify-end mt-1'>
            <span>{uploadProgress}%</span>
          </div>
        </div>
      )}
      <div className='flex flex-col w-full'>
          <h3 className='uppercase text-sm mb-1'>Tags:</h3>
          <ul className='flex flex-wrap'>
            {tags.map((tag, index) => (
              <div key={index} className='font-semibold bg-indigo-50 rounded-md mr-2 mt-2 px-3 py-2'>
                <li className='inline-block'>{tag}</li>
              </div>
            ))}
          </ul>
        </div>
    </div>
  );
};

export default DragAndDropUpload;
