import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

const DragAndDropUpload = () => {
  const [tags, setTags] = useState(["example-tag-1", "example-tag-2"]);
  const [isUploading, setIsUploading] = useState(false);

  const onDrop = useCallback((acceptedFiles) => {
    const file = acceptedFiles[0];
    const formData = new FormData();
    formData.append('video', file);

    setIsUploading(true);

    axios.post('http://localhost:5001/api/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
    .then(response => {
      setTags(response.data.tags);
    })
    .catch(error => {
      console.error('Error uploading the file:', error);
    })
    .finally(() => {
      setIsUploading(false);
    });
  }, []);

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({ 
    onDrop,
    noClick: true, 
    noKeyboard: true
  });

  return (
    <div className='flex flex-col items-center px-10 py-6 md:border-gray-200 md:border md:border-solid md:rounded-md max-w-screen-md mx-auto'>
      <h2 className='font-bold mb-1 text-xl self-start'>Upload your Video</h2>
      <p className='font-semibold text-sm mb-6 self-start text-gray-600'>Upload a video to generate metadata tags based on its transcript.</p>
      <div className={`flex flex-col items-center justify-center bg-gray-50 border-gray-400 border-2 rounded-md p-10 mb-4 w-full min-h-60 transition-colors duration-300 ${isDragActive ? 'bg-indigo-100/50 border-indigo-400 border-solid' : 'border-gray-400 border-dashed'}`} {...getRootProps()}>
        <input {...getInputProps()} />
        <div className='bg-indigo-300 p-2 rounded-md w-12 mb-4'>
          <svg width='100%' viewBox='0 0 15 15' fill='none' xmlns='http://www.w3.org/2000/svg'><path d='M7.81825 1.18188C7.64251 1.00615 7.35759 1.00615 7.18185 1.18188L4.18185 4.18188C4.00611 4.35762 4.00611 4.64254 4.18185 4.81828C4.35759 4.99401 4.64251 4.99401 4.81825 4.81828L7.05005 2.58648V9.49996C7.05005 9.74849 7.25152 9.94996 7.50005 9.94996C7.74858 9.94996 7.95005 9.74849 7.95005 9.49996V2.58648L10.1819 4.81828C10.3576 4.99401 10.6425 4.99401 10.8182 4.81828C10.994 4.64254 10.994 4.35762 10.8182 4.18188L7.81825 1.18188ZM2.5 9.99997C2.77614 9.99997 3 10.2238 3 10.5V12C3 12.5538 3.44565 13 3.99635 13H11.0012C11.5529 13 12 12.5528 12 12V10.5C12 10.2238 12.2239 9.99997 12.5 9.99997C12.7761 9.99997 13 10.2238 13 10.5V12C13 13.104 12.1062 14 11.0012 14H3.99635C2.89019 14 2 13.103 2 12V10.5C2 10.2238 2.22386 9.99997 2.5 9.99997Z' fill='currentColor' fill-rule='evenodd' clip-rule='evenodd'></path></svg>
        </div>
        <p className='font-bold text-lg text-center'>
          {`${isDragActive ? 'Drop to upload your file' : 'Drag a video file here to upload it for tagging'}`}
        </p>
        <button type='button'className={`font-semibold text-indigo-600 hover:cursor-pointer ${isDragActive && 'hidden'}`} onClick={open}>Or choose your file</button>
        {isUploading && <p>Uploading...</p>}
      </div>
      <div className='flex flex-col w-full'>
          <h3 className='uppercase text-sm'>Tags:</h3>
          <ul className='flex flex-wrap'>
            {tags.map((tag, index) => (
              <div key={index} className='bg-indigo-50 border-indigo-500 border border-solid rounded-md mr-2 mt-2 px-2 py-1'>
                <li className='inline-block'>{tag}</li>
              </div>
            ))}
          </ul>
        </div>
    </div>
  );
};

export default DragAndDropUpload;
