import React, { useState, useCallback, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { saveAs } from 'file-saver';
import LoadingSpinner from './LoadingSpinner';
import FileInfo from './FileInfo';
import uploadIcon from '../uploadIcon.svg';

const DragAndDropUpload = () => {
  const [tags, setTags] = useState([]);
  const [transcript, setTranscript] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [fileInfo, setFileInfo] = useState(null);
  const [ws, setWs] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    return () => {
      if (ws) {
        ws.close();
      }
    };
  }, [ws]);

  const onDrop = useCallback((acceptedFiles, rejectedFiles) => {
    if (rejectedFiles.length > 0) {
      const errorCode = rejectedFiles[0].errors[0].code;
      let errorMsg;
      if (errorCode === 'file-too-large') {
        errorMsg = "The file is too large. Please upload a video file smaller than 25MB.";
      } else if (errorCode === 'file-invalid-type') {
        errorMsg = "Invalid file type. Please upload a valid video file.";
      } else {
        errorMsg = "An error occurred while uploading the file. Please try again.";
      }
      setError(errorMsg);
      return;
    }

    if (ws) {
      ws.close();
      setWs(null);
    }

    const file = acceptedFiles[0];
    setFileInfo({
      name: file.name,
      size: (file.size / 1024 / 1024).toFixed(2),
    });

    setTags([]);
    setTranscript('');
    setUploadProgress(0);
    setIsUploading(true);
    setError(null);

    const formData = new FormData();
    formData.append('video', file);

    axios.post('http://localhost:5001/api/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
    .then(response => {
      const ws = new WebSocket('ws://localhost:5001');
      setWs(ws);

      ws.onopen = () => {
        ws.send(JSON.stringify({ action: 'process', filePath: response.data.filePath }));
      };

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.progress) {
          setUploadProgress(data.progress);
        }
        if (data.tags && data.transcript) {
          setTags(data.tags);
          setTranscript(data.transcript);
          setIsUploading(false);
        }
        if (data.error) {
          console.error(data.error);
          setIsUploading(false);
        }
      };

      ws.onclose = () => {
        setWs(null);
      };
    })
    .catch(error => {
      console.error('Error uploading the file:', error);
      setIsUploading(false);
    });
  }, [ws]);

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({ 
    onDrop,
    noClick: true, 
    noKeyboard: true,
    accept: 'video/mp4,video/x-m4v,video/*',
    maxSize: 25 * 1024 * 1024, // 25MB in bytes
    disabled: isUploading,
  });

  const getHighlightedText = (text, tags) => {
    if (!tags.length) return <span>{text}</span>;

    const sortedTags = tags.sort((a, b) => b.text.length - a.text.length);
    let parts = [text];

    sortedTags.forEach(tag => {
      const newParts = [];
      parts.forEach((part, index) => {
        if (typeof part === 'string') {
          const regex = new RegExp(`(${tag.text})`, 'gi');
          const splitParts = part.split(regex);
          splitParts.forEach((splitPart, idx) => {
            if (regex.test(splitPart)) {
              newParts.push(
                <span className="bg-yellow-200" key={`${tag.text}-${idx}`}>
                  {splitPart}
                  <br />
                  <small className="italic text-gray-500">{tag.category} ({tag.subCategory})</small>
                </span>
              );
            } else {
              newParts.push(<span key={`${splitPart}-${idx}`}>{splitPart}</span>);
            }
          });
        } else {
          newParts.push(part);
        }
      });
      parts = newParts;
    });

    return parts;
  };

  const formatTagsForCSV = (tags, fileName) => {
    const tagMap = tags.reduce((acc, tag) => {
      if (!acc[tag.category]) {
        acc[tag.category] = [];
      }
      acc[tag.category].push(tag.text);
      return acc;
    }, {});

    const categories = Object.keys(tagMap);
    const csvRows = ['Filename,' + categories.join(',')];
    const row = [fileName];

    categories.forEach(category => {
      row.push(`"${tagMap[category].join('|')}"`);
    });

    csvRows.push(row.join(','));
    return csvRows.join('\n');
  };

  const downloadCSV = () => {
    const csvData = formatTagsForCSV(tags, fileInfo.name);
    const blob = new Blob([csvData], { type: 'text/csv;charset=utf-8;' });
    saveAs(blob, 'tags.csv');
  };

  return (
    <div className='flex flex-col items-center max-w-screen-md px-10 py-6 mx-auto md:border-gray-200 md:border md:border-solid md:rounded-md'>
      <h2 className='self-start mb-1 text-xl font-bold'>Upload your Video</h2>
      <p className='self-start mb-6 text-sm font-semibold text-gray-600'>Upload a video to generate metadata tags based on its transcript.</p>
      {error && <p className="text-red-500">{error}</p>}
      <div 
        className={`flex flex-col items-center justify-center bg-gray-50 border-gray-400 border-2 rounded-md p-10 mb-6 w-full min-h-60 transition-colors duration-300 ${isDragActive ? 'bg-indigo-100/50 border-indigo-400 border-solid' : 'border-gray-400 border-dashed'} ${isUploading ? 'opacity-50 cursor-not-allowed' : ''}`} 
        {...getRootProps()}
      >
        <input {...getInputProps()} disabled={isUploading} />
        {
          isUploading ? (
            <LoadingSpinner />
          ) : (
            <div className='flex flex-col items-center'>
              <div className='w-12 p-2 mb-4 bg-indigo-500 rounded-md'>
                <img src={uploadIcon} alt="upload icon" />
              </div>
              <p className='text-lg font-bold text-center'>
                {`${isDragActive ? 'Drop to upload your file' : 'Drag a video file here to upload it for tagging'}`}
              </p>
              <button type='button'className={`font-semibold text-indigo-600 hover:cursor-pointer ${isDragActive && 'hidden'}`} onClick={open}>Or choose your file</button>
            </div>
          )
        }
        {isUploading && <p>Uploading...</p>}
      </div>
      {fileInfo && <FileInfo fileInfo={fileInfo} uploadProgress={uploadProgress} />}
      <div className='flex flex-col w-full'>
          <h3 className='mb-1 text-sm uppercase'>Transcript:</h3>
          <div className='p-4 mb-6 bg-gray-100 rounded-md'>
            {getHighlightedText(transcript, tags)}
          </div>
          <h3 className='mb-1 text-sm uppercase'>Tags:</h3>
          <ul className='flex flex-wrap'>
            {tags.map((tag, index) => (
              <div key={`${tag.text}-${index}`} className='px-3 py-2 mt-2 mr-2 font-semibold rounded-md bg-indigo-50'>
                <li className='inline-block'>
                  <strong>{tag.text}</strong> - {tag.category} ({tag.subCategory})
                  <br/>
                  <small>Confidence: {tag.confidenceScore.toFixed(2)}</small>
                </li>
              </div>
            ))}
          </ul>
          <button 
            onClick={downloadCSV} 
            className='px-4 py-2 mt-4 text-white bg-indigo-500 rounded-md'
            disabled={!fileInfo}
          >
            Download Tags as CSV
          </button>
        </div>
    </div>
  );
};

export default DragAndDropUpload;
