import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

const DragAndDropUpload = () => {
  const [tags, setTags] = useState(["example-tag-1", "example-tag-2"]);
  const [isUploading, setIsUploading] = useState(false);

  const onDrop = useCallback((acceptedFiles) => {
    const file = acceptedFiles[0];
    const formData = new FormData();
    formData.append('video', file);

    setIsUploading(true);

    axios.post('http://localhost:5001/api/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
    .then(response => {
      setTags(response.data.tags);
    })
    .catch(error => {
      console.error('Error uploading the file:', error);
    })
    .finally(() => {
      setIsUploading(false);
    });
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });

  return (
    <div className='flex flex-col items-center p-10 border-gray-100 border border-solid rounded-md max-w-screen-md mx-auto'>
      <h2 className='font-bold mb-4'>Upload your Video</h2>
      <div className='bg-gray-100 border-gray-400 border-dashed border rounded-md p-10 mb-4' {...getRootProps()}>
        <input {...getInputProps()} />
        {
          isDragActive ?
          <p>Drop the files here ...</p> :
          <p>Drag files here to upload them for tagging, or <span className="font-semibold underline hover:cursor-pointer" onClick={open}>choose your files</span></p>
        }
        {isUploading && <p>Uploading...</p>}
      </div>
      <div className='flex flex-col w-full'>
          <h3 className='uppercase text-sm'>Tags:</h3>
          <ul className='flex flex-wrap'>
            {tags.map((tag, index) => (
              <div className='bg-blue-50 border-blue-500 border border-solid rounded-md mr-2 mt-2 px-2 py-1'>
                <li className='inline-block' key={index}>{tag}</li>
              </div>
            ))}
          </ul>
        </div>
    </div>
  );
};

export default DragAndDropUpload;
